$(document).ready(function(){$('#chatbot-form').on('submit',function(event){event.preventDefault();const question=$('#question').val();if(!question.trim()){alert("Please enter a question.");return}
$.ajax({url:'/.netlify/functions/chatbot-answer',type:'POST',contentType:'application/json',data:JSON.stringify({question}),success:function(response){$('#answer').html(`<p>${response.answer}</p>`)},error:function(xhr,status,error){console.error("Status: "+status);console.error("Error: "+error);console.error("Response Text: "+xhr.responseText);alert("An error occurred while processing your request. Please try again.")}})});$('#darkModeToggle').on('click',function(){$('body').toggleClass('dark-mode');$('.navbar, .footer').toggleClass('dark-mode');$('#chatbot-form input, #chatbot-form textarea').toggleClass('dark-mode-input')})})