document.addEventListener("DOMContentLoaded",function(){function showLoading(){document.getElementById('loading').classList.add('loading-visible')}
function hideLoading(){document.getElementById('loading').classList.remove('loading-visible')}
function updateFooterTextColor(){document.querySelectorAll('footer a').forEach(link=>{link.classList.toggle('text-white',document.body.classList.contains('dark-mode'));link.classList.toggle('text-dark',!document.body.classList.contains('dark-mode'))})}
function applyDarkMode(isDarkMode){const elementsToToggle=[document.body,document.querySelector('nav'),document.querySelector('footer'),document.querySelector('.bg-image'),document.querySelector('.navbar')];elementsToToggle.forEach(element=>{if(element){element.classList.toggle('dark-mode',isDarkMode)}});document.querySelectorAll('a').forEach(link=>link.classList.toggle('dark-mode',isDarkMode));document.querySelectorAll('.hero-title').forEach(element=>{if(element){element.classList.toggle('dark-mode-text',isDarkMode)}});document.querySelectorAll('.text-dark').forEach(element=>{if(element){element.classList.toggle('text-white',isDarkMode);element.classList.toggle('text-dark',!isDarkMode)}});updateFooterTextColor()}
function handleThemeToggle(){showLoading();setTimeout(()=>{const isDarkMode=document.body.classList.toggle('dark-mode');applyDarkMode(isDarkMode);if(isDarkMode){localStorage.setItem('theme','dark')}else{localStorage.removeItem('theme')}
hideLoading()},500)}
const savedTheme=localStorage.getItem('theme');if(savedTheme==='dark'){applyDarkMode(!0)}
document.getElementById('darkModeToggle').addEventListener('click',handleThemeToggle);updateFooterTextColor()})